	/**
	 * Constructor
	 * @param args :[0]=name of file to be processed
	 */
	public Modeler(String[] args, Application app) {
		enableEvents(AWTEvent.WINDOW_EVENT_MASK);
		try {
			application = app;
			jbInit();  //Initialize components//
			if (args.length > 0) {
				processContentsFile(args[0]);
			} else {
				processContentsFile("");
			}
		}
		catch(Exception e) {
			e.printStackTrace();
		}
	}

