			nodeJumpButton[1] = null;
			nodeJumpButton[2] = null;
			nodeJumpButton[3] = null;
			nodeJumpButton[4] = null;
			nodeJumpButton[5] = null;
			nodeJumpButton[6] = null;
			nodeJumpButton[7] = null;
