	/**
	 * arrange order of DOM elements according to the XEAD-format specifications
	 */
	void arrangeOrderOfDomElement() {
		org.w3c.dom.Element topElement;
		//org.w3c.dom.Node parentDomNode;
		NodeList nodeList;
		//
		nodeList = domDocument.getElementsByTagName("Function");
		if (nodeList.getLength() > 0) {
			topElement = (org.w3c.dom.Element)nodeList.item(0);
			topElement = sortElementsByType("Relationship", topElement);
			topElement = sortElementsByType("Table", topElement);
			topElement = sortElementsByType("Subsystem", topElement);
			topElement = sortElementsByType("Task", topElement);
			topElement = sortElementsByType("Role", topElement);
			topElement = sortElementsByType("SubjectArea", topElement);
			topElement = sortElementsByType("MaintenanceLog", topElement);
		} else {
			nodeList = domDocument.getElementsByTagName("Relationship");
			if (nodeList.getLength() > 0) {
				topElement = (org.w3c.dom.Element)nodeList.item(0);
				topElement = sortElementsByType("Table", topElement);
				topElement = sortElementsByType("Subsystem", topElement);
				topElement = sortElementsByType("Task", topElement);
				topElement = sortElementsByType("Role", topElement);
				topElement = sortElementsByType("SubjectArea", topElement);
				topElement = sortElementsByType("MaintenanceLog", topElement);
			} else {
				nodeList = domDocument.getElementsByTagName("Table");
				if (nodeList.getLength() > 0) {
					topElement = (org.w3c.dom.Element)nodeList.item(0);
					topElement = sortElementsByType("Subsystem", topElement);
					topElement = sortElementsByType("Task", topElement);
					topElement = sortElementsByType("Role", topElement);
					topElement = sortElementsByType("SubjectArea", topElement);
					topElement = sortElementsByType("MaintenanceLog", topElement);
				} else {
					nodeList = domDocument.getElementsByTagName("Subsystem");
					if (nodeList.getLength() > 0) {
						topElement = (org.w3c.dom.Element)nodeList.item(0);
						topElement = sortElementsByType("Task", topElement);
						topElement = sortElementsByType("Role", topElement);
						topElement = sortElementsByType("SubjectArea", topElement);
						topElement = sortElementsByType("MaintenanceLog", topElement);
					} else {
						nodeList = domDocument.getElementsByTagName("Task");
						if (nodeList.getLength() > 0) {
							topElement = (org.w3c.dom.Element)nodeList.item(0);
							topElement = sortElementsByType("Role", topElement);
							topElement = sortElementsByType("SubjectArea", topElement);
							topElement = sortElementsByType("MaintenanceLog", topElement);
						} else {
							nodeList = domDocument.getElementsByTagName("Role");
							if (nodeList.getLength() > 0) {
								topElement = (org.w3c.dom.Element)nodeList.item(0);
								topElement = sortElementsByType("SubjectArea", topElement);
								topElement = sortElementsByType("MaintenanceLog", topElement);
							} else {
								nodeList = domDocument.getElementsByTagName("SubjectArea");
								if (nodeList.getLength() > 0) {
									topElement = (org.w3c.dom.Element)nodeList.item(0);
									topElement = sortElementsByType("MaintenanceLog", topElement);
								}
							}
						}
					}
				}
			}
		}
	}
	/**
	 * sort elements with specific type
	 * @param type :type of elements to be sorted
	 * @param topElement :element where sorted element is inserted before
	 * @return Element :top element of sorted
	 */
	org.w3c.dom.Element sortElementsByType(String type, org.w3c.dom.Element topElement) {
		org.w3c.dom.Element element;
		NodeList nodeList = domDocument.getElementsByTagName(type);
		for (int i = nodeList.getLength()-1; i >= 0; i--) {
			element = (org.w3c.dom.Element)nodeList.item(i);
			systemNode.getElement().insertBefore(element, topElement);
			topElement = element;
		}
		return topElement;
	}
	/**
	 * purge elements of SubsystemTable with deleted Table
	 */
	void purgeInvalidSubsystemTable() {
		org.w3c.dom.Element element;
		org.w3c.dom.Element[] elementArray1 = new org.w3c.dom.Element[1000];
		int arrayIndex1 = -1;
		org.w3c.dom.Node parentDomNode;
		//
		//Save invalid subsystem table into the array//
		NodeList nodeList = domDocument.getElementsByTagName("SubsystemTable");
		for (int i = 0; i < nodeList.getLength(); i++) {
			element = (org.w3c.dom.Element)nodeList.item(i);
			if (getSpecificXeadTreeNode("Table", element.getAttribute("TableID"), null) == null) {
				arrayIndex1 = arrayIndex1 + 1;
				elementArray1[arrayIndex1] = element;
			}
		}
		//
		//Purge subsystem table//
		for (int i = 0; i <= arrayIndex1; i++) {
			parentDomNode = elementArray1[i].getParentNode();
			parentDomNode.removeChild(elementArray1[i]);
		}
	}
	/**
	 * purge elements of SubsystemRelationship with deleted Relationship
	 */
	void purgeInvalidSubsystemRelationship() {
		org.w3c.dom.Element subsystemRelationshipElement, relationshipElement;
		NodeList subsystemRelationshipList, relationshipList;
		org.w3c.dom.Element[] elementArray = new org.w3c.dom.Element[1000];
		int arrayIndex = -1;
		org.w3c.dom.Node parentDomNode;
		boolean validSubsystemRelationship = false;
		//
		//Save invalid subsystem relationship into the array//
		relationshipList = domDocument.getElementsByTagName("Relationship");
		subsystemRelationshipList = domDocument.getElementsByTagName("SubsystemRelationship");
		jProgressBar.setMaximum(subsystemRelationshipList.getLength());
		for (int i = 0; i < subsystemRelationshipList.getLength(); i++) {
			subsystemRelationshipElement = (org.w3c.dom.Element)subsystemRelationshipList.item(i);
			validSubsystemRelationship = false;
			for (int j = 0; j < relationshipList.getLength(); j++) {
				relationshipElement = (org.w3c.dom.Element)relationshipList.item(j);
				if (subsystemRelationshipElement.getAttribute("RelationshipID").equals(relationshipElement.getAttribute("ID"))) {
					validSubsystemRelationship = true;
					break;
				}
			}
			if (!validSubsystemRelationship) {
				arrayIndex = arrayIndex + 1;
				elementArray[arrayIndex] = subsystemRelationshipElement;
			}
			jProgressBar.setValue(jProgressBar.getValue()+1);
			jProgressBar.paintImmediately(0,0,jProgressBar.getWidth(),jProgressBar.getHeight());
		}
		jProgressBar.setValue(0);
		//
		//Purge subsystem relationship//
		for (int i = 0; i <= arrayIndex; i++) {
			parentDomNode = elementArray[i].getParentNode();
			parentDomNode.removeChild(elementArray[i]);
		}
	}
