	/**
	 * [Tool|Jump to URL] Jump to Page of URL where mouse is pointing
	 * @param e :Action Event
	 */
	void jMenuItemToolJumpToURLPage_actionPerformed(ActionEvent e) {
		if (urlStringIsValid()) {
			try {
				if (urlString.substring(0, 20).equals("file:///currentpath/")) {
					File file = new File(currentFileName);
					urlString = "file:///" + file.getParent() + File.separator + urlString.substring(20, urlString.length());
				}
				try {
					setCursor(new Cursor(Cursor.WAIT_CURSOR));
					desktop.browse(new URI(urlString));
				} catch (Exception ex) {
				} finally {
					setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
				}
			}
			catch (Exception ex) {
				JOptionPane.showMessageDialog(this.getContentPane(), "URL " + urlString + res.getString("S1617"));
			}
		} else {
			JOptionPane.showMessageDialog(this.getContentPane(), res.getString("S1618") + "\n" + urlString + "\n" +
					res.getString("S1621") + "\n" +
					res.getString("S1623"));
		}
		urlString = "";
	}
	/**
	 * [Help|Help] Launch Browser to open Index.html(Help file)
	 * @param e :Action Event
	 */
	void jMenuItemHelpHelp_actionPerformed(ActionEvent e) {
		String applicationFolder = System.getProperty("java.class.path");
		int pos = applicationFolder.lastIndexOf(System.getProperty("file.separator"));
		applicationFolder = applicationFolder.substring(0,pos);
		File helpFile = new File(applicationFolder + "\\help\\Index.html");
		if (helpFile.exists()) {
			try {
				setCursor(new Cursor(Cursor.WAIT_CURSOR));
				desktop.open(helpFile);
			} catch (Exception ex) {
			} finally {
				setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
			}
		} else {
			JOptionPane.showMessageDialog(this.getContentPane(), res.getString("S1628") + helpFile.getPath() + res.getString("S1629"));
		}
	}
	/**
	 * [Help|About]
	 * @param e :Action Event
	 */
	void jMenuItemHelpAbout_actionPerformed(ActionEvent e) {
		DialogAbout dlg = new DialogAbout(this);
		Dimension dlgSize = dlg.getPreferredSize();
		Dimension frmSize = getSize();
		Point loc = getLocation();
		dlg.setLocation((frmSize.width - dlgSize.width) / 2 + loc.x, (frmSize.height - dlgSize.height) / 2 + loc.y);
		dlg.setModal(true);
		dlg.pack();
		dlg.setVisible(true);
	}
	/**
	 * [Scan|Scan]
	 * @param e :Action Event
	 */
	void jMenuItemScanString_actionPerformed(ActionEvent e) {
		int rtn1 = 0;
		int rtn2 = 0;
		//
		currentMainTreeNode.updateFields();
		if (changeState.isChanged()) {
			Object[] bts = {res.getString("S1111"), res.getString("S1100")} ;
			rtn1 = JOptionPane.showOptionDialog(this, res.getString("S1002"),
					res.getString("S1118"), JOptionPane.DEFAULT_OPTION, JOptionPane.INFORMATION_MESSAGE, null, bts, bts[0]);
			if (rtn1 == 0) {
				saveFileWithCurrentFileName();
				xeadUndoManager.resetLog();
				changeState.setChanged(false);
			}
		}
		//
		if (rtn1 == 0) {
			boolean scanAndUpdated = dialogScan.request();
			if (scanAndUpdated) {
				Object[] bts = {res.getString("S1111"), res.getString("S1112"), res.getString("S1113")} ;
				rtn2 = JOptionPane.showOptionDialog(this, res.getString("S1001"),
						res.getString("S1000"), JOptionPane.DEFAULT_OPTION, JOptionPane.INFORMATION_MESSAGE, null, bts, bts[0]);
				//
				//Save changes//
				//
				if (rtn2 == 0) {
					saveFileWithCurrentFileName();
					xeadUndoManager.resetLog();
					changeState.setChanged(false);
				}
				//
				//Save changes as other name//
				//
				if (rtn2 == 1) {
					String name = specifyNameOfNewFile(res.getString("S788"),res.getString("S1125"), currentFileName);
					if (!name.equals("")) {
						currentFileName = name;
						this.setTitle(DialogAbout.APPLICATION_NAME + " - [" + currentFileName + "] - " + systemName);
						saveFileWithCurrentFileName();
						changeState.setChanged(false);
						xeadUndoManager.resetLog();
					}
				}
				//
				//Setup MainTreeNode//
				//
				try {
					setupMainTreeModelWithCurrentFileName();
				}
				catch(Exception exception) {
					exception.printStackTrace();
				}
			}
		}
	}
