	/**
	 * Event Handler for jTreeMain in case key Released
	 * @param e :Key Event
	 **/
	void jTreeMain_keyReleased(KeyEvent e) {
		if ((e.getModifiersEx() & InputEvent.CTRL_DOWN_MASK) != 0) {
			if (e.getKeyCode() == KeyEvent.VK_C) {
				jMenuItemXeadTreeNodeCopy_actionPerformed(null);
			}
			if (e.getKeyCode() == KeyEvent.VK_V) {
				jMenuItemXeadTreeNodePaste_actionPerformed(null);
			}
			if (e.getKeyCode() == KeyEvent.VK_X) {
				jMenuItemXeadTreeNodeCut_actionPerformed(null);
			}
		} else {
			if (e.getKeyCode() == KeyEvent.VK_DELETE) {
				jMenuItemXeadTreeNodeDelete_actionPerformed(null);
			} else {
				TreePath tp = jTreeMain.getSelectionPath();
				setupContentsPaneForTreeNodeSelected((XeadTreeNode)tp.getLastPathComponent(), false);
			}
		}
	}
	/**
	 * Event Handler for jTreeMain in case mouse Pressed
	 * @param e :Mouse Event
	 */
	void jTreeMain_mousePressed(MouseEvent e) {
		dragStartPointX = e.getX();
		dragStartPointY = e.getY();
		int row = jTreeMain.getRowForLocation(e.getX(),e.getY());
		if (row > -1) {
			TreePath tp = jTreeMain.getPathForRow(row);
			dragSourceMainTreeNode = (XeadTreeNode)tp.getLastPathComponent();
			jTreeMain.setSelectionPath(tp);
		}
	}
	/**
	 * Event Handler for jTreeMain in case mouse Released
	 * @param e :Mouse Event
	 */
	void jTreeMain_mouseReleased(MouseEvent e) {
		//
		//Check component where dragging has ended//
		Point viewPosition = jScrollPaneTreeView.getViewport().getViewPosition();
		Component comp = jSplitPaneMain.findComponentAt(e.getX() - viewPosition.x, e.getY() - viewPosition.y);
		if (comp != null) {
			if (comp.equals(jTreeMain)) {
				//
				//Setup contents pane for the node selected//
				TreePath tp = jTreeMain.getSelectionPath();
				if ((XeadTreeNode)tp.getLastPathComponent() != currentMainTreeNode) {
					setupContentsPaneForTreeNodeSelected((XeadTreeNode)tp.getLastPathComponent(), true);
				}
				//
				//Right Button Pressed//
				if ((e.getModifiers() & InputEvent.BUTTON1_MASK) != InputEvent.BUTTON1_MASK) {
					showPopupXeadTreeNode(e);
				}
			} else {
				if (dragSourceMainTreeNode != null) {
					//
					//Drag and Drop Processing//
					boolean jTreeMainRefreshRequired = dragSourceMainTreeNode.dragAndDropNodeIntoComponent(dropTargetComponent, e);
					dragSourceMainTreeNode = null;
					//
					//Setup contents pane for the current node//
					jTreeMain.setSelectionPath(new TreePath(currentMainTreeNode.getPath()));
					setupContentsPaneForTreeNodeSelected(currentMainTreeNode, jTreeMainRefreshRequired);
				}
			}
		}
		//
		//Reset cursor shape to default//
		setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
	}
	/**
	 * Event Handler for jTreeMain in case mouse Dragged
	 * @param e :Mouse Event
	 */
	void jTreeMain_mouseDragged(MouseEvent e) {
		int int1 = (e.getX() - dragStartPointX) * (e.getX() - dragStartPointX);
		int int2 = (e.getY() - dragStartPointY) * (e.getY() - dragStartPointY);
		if (int1 + int2 > 100) {
			Point viewPosition = jScrollPaneTreeView.getViewport().getViewPosition();
			dropTargetComponent = jSplitPaneMain.findComponentAt(e.getX() - viewPosition.x, e.getY() - viewPosition.y);
			if (((e.getModifiers() & java.awt.event.MouseEvent.BUTTON1_MASK) != 0)
					&& dragSourceMainTreeNode != null && dropTargetComponent != null) {
				setCursor(dragSourceMainTreeNode.getDragAndDropCursorForComponent(dropTargetComponent));
			}
		} else {
			setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
		}
	}
