		//
		//Calculate shape of arrow according to its direction//
		public Shape getShapeOfArrow(int arrowDirection) {
			int[] x = new int[]{7,0,14};
			int[] y = new int[]{0,10,10};
			//
			switch (arrowDirection) {
			case 0: //North//
				x = new int[]{ 4,7,11};
				y = new int[]{14,0,14};
				break;
			case 1: //NNE//
				x = new int[]{ 5,11,14};
				y = new int[]{10,13, 0};
				break;
			case 2: //NE//
				x = new int[]{2,14, 7};
				y = new int[]{7, 0,12};
				break;
			case 3: //EEN//
				x = new int[]{1,14,4};
				y = new int[]{4, 0,9};
				break;
			case 4: //East//
				x = new int[]{0,14, 0};
				y = new int[]{5, 8,11};
				break;
			case 5: //EES//
				x = new int[]{4,14, 2};
				y = new int[]{5,14,11};
				break;
			case 6: //SE//
				x = new int[]{2,7,14};
				y = new int[]{7,2,14};
				break;
			case 7: //SSE//
				x = new int[]{10,5,14};
				y = new int[]{ 2,4,14};
				break;
			case 8: //South//
				x = new int[]{4,7,10};
				y = new int[]{0,14,0};
				break;
			case 9: //SSW//
				x = new int[]{3,0,9};
				y = new int[]{1,14,3};
				break;
			case 10: //SW//
				x = new int[]{0,7,12};
				y = new int[]{14,2,7};
				break;
			case 11: //WWS//
				x = new int[]{0,10,13};
				y = new int[]{14,5,11};
				break;
			case 12: //West//
				x = new int[]{0,14,14};
				y = new int[]{8, 5,11};
				break;
			case 13: //WWN//
				x = new int[]{0,13,10};
				y = new int[]{0, 2, 9};
				break;
			case 14: //NW//
				x = new int[]{0,12, 7};
				y = new int[]{0, 7,12};
				break;
			case 15: //NNW//
				x = new int[]{0, 2, 9};
				y = new int[]{0,13,10};
				break;
			}
			return new Polygon(x,y,3);
		}
