		//
		private void jLabelArrow_mouseDragged(MouseEvent e) {
			if (clickable_) {
				int powerredX = 0;
				int powerredY = 0;
				int minimumPowerredDistance = 2147483647;
				int mousePosX = jLabelPressed.getBounds().x + e.getX();
				int mousePosY = jLabelPressed.getBounds().y + e.getY();
				//
				//Search the nearest arrow-position-index(0 - 9)//
				//on DataflowNode from mouse pointer            //
				for (int i = 0; i <= 9; i++) {
					powerredX = (arrowCordinateArray[i].x - mousePosX) * (arrowCordinateArray[i].x - mousePosX);
					powerredY = (arrowCordinateArray[i].y - mousePosY) * (arrowCordinateArray[i].y - mousePosY);
					if (powerredX + powerredY <= minimumPowerredDistance) {
						minimumPowerredDistance = powerredX + powerredY;
						terminalPosIndex = i;
					}
				}
				jLabelArrowMoveGuide.setBounds(new Rectangle(arrowCordinateArray[terminalPosIndex].x, arrowCordinateArray[terminalPosIndex].y, 15, 15));
			}
		}
