			jLabelIconMoveGuide.setLayout(null);
			jLabelIconMoveGuide.setBorder(BorderFactory.createLineBorder(Color.WHITE));
			jLabelIconMoveGuide.setOpaque(false);
			//
			//Retrieve entityBox1&2 and their position//
			for (int i = 0; i < datamodelEntityBoxArray.size(); i++) {
				if (datamodelEntityBoxArray.get(i).getTableID().equals(relationshipElement.getAttribute("Table1ID"))) {
					entityBox1 = datamodelEntityBoxArray.get(i);
				}
				if (datamodelEntityBoxArray.get(i).getTableID().equals(relationshipElement.getAttribute("Table2ID"))) {
					entityBox2 = datamodelEntityBoxArray.get(i);
				}
			}
			//
			//Retrieve position-index of terminals specified by subsystem//
			terminalIndex1 = Integer.parseInt(subsystemRelationshipElement_.getAttribute("TerminalIndex1"));
			terminalIndex2 = Integer.parseInt(subsystemRelationshipElement_.getAttribute("TerminalIndex2"));
			if (terminalIndex1 == -1 || terminalIndex2 == -1) {
				boxPoint1 = entityBox1.getBoxPositionPoint();
				boxPoint2 = entityBox2.getBoxPositionPoint();
				//1:left-above 2:right-below//
				if (boxPoint1.x <= boxPoint2.x && boxPoint1.y <= boxPoint2.y) {
					terminalIndex1 = 17;
					terminalIndex2 = 20;
				}
				//1:right-above 2:left-below//
				if (boxPoint1.x > boxPoint2.x && boxPoint1.y <= boxPoint2.y) {
					terminalIndex1 = 20;
					terminalIndex2 = 23;
				}
				//1:left-below 2:right-above//
				if (boxPoint1.x <= boxPoint2.x && boxPoint1.y > boxPoint2.y) {
					terminalIndex1 = 23;
					terminalIndex2 = 20;
				}
				//1:right-below 2:left-above//
				if (boxPoint1.x > boxPoint2.x && boxPoint1.y > boxPoint2.y) {
					terminalIndex1 = 20;
					terminalIndex2 = 17;
				}
			}
			//
			if (subsystemRelationshipElement_.getAttribute("ShowOnModel").equals("false")) {
				showOnModel = false;
			}
			//
			setupTerminalIcons();
