		//
		//Calculate and set TextPane preferredSize //
		private Dimension getTextPanePixelSize(int fontSize, int numberOfCharX, int numberOfCharY) {
			Dimension preferredSize = new Dimension(0, 0);
			int pixelSizeX = 500;
			int pixelSizeY = 400;
			if (fontSize == 10) {
				pixelSizeX = numberOfCharX * 5 + 6;
				pixelSizeY = numberOfCharY * 14 + 6;
			}
			if (fontSize == 12) {
				pixelSizeX = numberOfCharX * 6 + 6;
				pixelSizeY = numberOfCharY * 17 + 6;
			}
			if (fontSize == 14) {
				pixelSizeX = numberOfCharX * 7 + 6;
				pixelSizeY = numberOfCharY * 20 + 6;
			}
			preferredSize.setSize(pixelSizeX, pixelSizeY);
			return preferredSize;
		}
		//
		//Set Character Attribute to StyledDocument//
		private void setTextStyleToDocument(StyledDocument styledDocument, NodeList textStyleList) {
			int start = 0;
			int end = 0;
			String type, color;
			for (int i = 0; i < textStyleList.getLength(); i++) {
				org.w3c.dom.Element elementTextStyle = (org.w3c.dom.Element)textStyleList.item(i);
				Style style = styledDocument.addStyle("style1",null);
				StringTokenizer workTokenizer = new StringTokenizer(elementTextStyle.getAttribute("Value"), "," );
				start = Integer.parseInt(workTokenizer.nextToken());
				end = Integer.parseInt(workTokenizer.nextToken());
				type = workTokenizer.nextToken();
				if (type.equals("BG")) {
					color = workTokenizer.nextToken();
					StyleConstants.setBackground(style, stringToColor(color));
					styledDocument.setCharacterAttributes(start, end - start, style, false);
				}
				if (type.equals("FG")) {
					color = workTokenizer.nextToken();
					StyleConstants.setForeground(style, stringToColor(color));
					styledDocument.setCharacterAttributes(start, end - start, style, false);
				}
				if (type.equals("UL")) {
					StyleConstants.setUnderline(style, true);
					for (int j = 0; j < end - start; j++) {
						styledDocument.setCharacterAttributes(start + j, 1, style, false);
					}
				}
			}
		}
		//
		//Get Not-Null constraints of Table-Key Specified with its ID//
		private boolean getNotNullOfTableKey() {
			boolean notNull = true;
			XeadTreeNode node1, node2, node3;
			org.w3c.dom.Element keyFieldElement;
			if (this.getType() == "TableKey") {
				node1 = (XeadTreeNode)this.getParent(); //node1:Node of KeyList//
				node2 = (XeadTreeNode)node1.getPreviousSibling(); //node2:Node of FieldList//
				NodeList keyFieldList = this.getElement().getElementsByTagName("TableKeyField");
				for (int i = 0; i < keyFieldList.getLength(); i++) {
					keyFieldElement = (org.w3c.dom.Element)keyFieldList.item(i);
					for (int j = 0; j < node2.getChildCount(); j++) {
						node3 = (XeadTreeNode) node2.getChildAt(j); //node3:Node of Field
						if (node3.getElement().getAttribute("ID").equals(keyFieldElement.getAttribute("FieldID"))) {
							if (!node3.getElement().getAttribute("NotNull").equals("true")) {
								notNull = false;
							}
							break;
						}
					}
				}
			}
			return notNull;
		}
