		//
		private org.w3c.dom.Element replaceRelationshipAndCloneElement(String tableID, String keyID, org.w3c.dom.Element replaceElement) {
			org.w3c.dom.Element cloneElement = null;
			//
			//Remove all of current relationship and subsystem-relationship related to the key//
			if (nodeType_.equals("TableKey")) {
				org.w3c.dom.Element relationshipElement;
				NodeList currentRelationshipList = systemNode.getElement().getElementsByTagName("Relationship");
				int rowIndex1 = 0;
				int rowNumber1 = currentRelationshipList.getLength();
				for (int i = 0; i < rowNumber1; i++) {
					relationshipElement = (org.w3c.dom.Element)currentRelationshipList.item(rowIndex1);
					if (tableID.equals(relationshipElement.getAttribute("Table1ID")) && keyID.equals(relationshipElement.getAttribute("TableKey1ID")) ||
							tableID.equals(relationshipElement.getAttribute("Table2ID")) && keyID.equals(relationshipElement.getAttribute("TableKey2ID")) ) {
						systemNode.getElement().removeChild(relationshipElement);
					} else {
						rowIndex1 = rowIndex1 + 1;
					}
				}
				//
				//Replace nodes of relationship//
				cloneElement = (org.w3c.dom.Element)replaceElement.cloneNode(true);
				NodeList oldRelationshipList = cloneElement.getElementsByTagName("Relationship");
				int rowNumber = oldRelationshipList.getLength();
				for (int i = 0; i < rowNumber; i++) {
					systemNode.getElement().appendChild(oldRelationshipList.item(0));
				}
			}
			return cloneElement;
		}
