		//
		public void updateIndentLevelOfTaskActions(XeadTreeNode rootOfNodesBeingUpdated, int valueOfIndentLevelToBeAdjusted) {
			org.w3c.dom.Element element;
			XeadTreeNode childNode;
			XeadTreeNode[] nodeArray = new XeadTreeNode[100];
			int j = 0;
			int k = 0;
			int indentLevel = 0;
			//
			//Put all children into node array//
			childNode = rootOfNodesBeingUpdated;
			nodeArray[0] = childNode;
			while (childNode != null) {
				for (int i = 0; i < childNode.getChildCount(); i++) {
					j = j+1;
					nodeArray[j] = (XeadTreeNode)childNode.getChildAt(i);
				}
				k = k+1;
				childNode = nodeArray[k];
			}
			//
			//Update DOM element of those children node//
			sortableDomElementListModel.removeAllElements();
			for (int i = 0; i < k; i++) {
				element = (org.w3c.dom.Element)nodeArray[i].getElement();
				indentLevel = Integer.parseInt(element.getAttribute("IndentLevel")) + valueOfIndentLevelToBeAdjusted;
				element.setAttribute("IndentLevel", Integer.toString(indentLevel));
			}
		}
