		//
		private boolean isDuplicatedStyle(org.w3c.dom.Element newElement, NodeList existingStyleList) {
			boolean result = false;
			org.w3c.dom.Element existingElement = null;
			String existingType = "";
			String existingColor = "";
			int existingPositionFrom = 0;
			int existingPositionThru = 0;
			//
			StringTokenizer workTokenizer = new StringTokenizer(newElement.getAttribute("Value"), "," );
			int newPositionFrom = Integer.parseInt(workTokenizer.nextToken());
			int newPositionThru = Integer.parseInt(workTokenizer.nextToken());
			String newType = workTokenizer.nextToken();
			String newColor;
			if (newType.equals("BG") || newType.equals("FG")) {
				newColor = workTokenizer.nextToken();
			} else {
				newColor = "";
			}
			//
			for (int i = 0; i < existingStyleList.getLength(); i++) {
				existingElement = (org.w3c.dom.Element)existingStyleList.item(i);
				workTokenizer = new StringTokenizer(existingElement.getAttribute("Value"), "," );
				existingPositionFrom = Integer.parseInt(workTokenizer.nextToken());
				existingPositionThru = Integer.parseInt(workTokenizer.nextToken());
				existingType = workTokenizer.nextToken();
				if (existingType.equals("BG") || existingType.equals("FG")) {
					existingColor = workTokenizer.nextToken();
				} else {
					existingColor = "";
				}
				//
				if ((existingType.equals(newType)) && (existingColor.equals(newColor))) {
					if ((existingPositionFrom <= newPositionFrom) && (existingPositionThru >= newPositionThru)) {
						result = true;
					}
				}
			}
			//
			return result;
		}
