	/**
	 * Method to concat lines into a single string value with EOL mark
	 * @param originalString :original string value to be processed
	 * @return String :string value of processed string
	 */
	static String concatLinesWithTokenOfEOL(String originalString) {
		StringBuffer processedString = new StringBuffer();
		int lastEnd = 0;
		for (int i = 0; i <= originalString.length(); i++) {
			if (i < originalString.length()) {
				if (originalString.substring(i,i+1).equals("\n")) {
					processedString.append(originalString.substring(lastEnd, i));
					processedString.append("#EOL#");
					lastEnd = i+1;
				}
			} else {
				processedString.append(originalString.substring(lastEnd, i));
			}
		}
		return processedString.toString();
	}
	/**
	 * Method to process String value with EOL mark
	 * @param originalString :original string value to be processed
	 * @param stringToBeInserted :value to be replaced into "#EOL#"(usually it's "\n")
	 * @return String :string value of processed string
	 */
	static String substringLinesWithTokenOfEOL(String originalString, String stringToBeInserted) {
		StringBuffer processedString = new StringBuffer();
		int lastEnd = 0;
		for (int i = 0; i <= originalString.length(); i++) {
			if (i+5 <= originalString.length()) {
				if (originalString.substring(i,i+5).equals("#EOL#")) {
					processedString.append(originalString.substring(lastEnd, i));
					processedString.append(stringToBeInserted);
					lastEnd = i+5;
				}
			} else {
				if (i == originalString.length()) {
					processedString.append(originalString.substring(lastEnd, i));
				}
			}
		}
		return processedString.toString();
	}
	/**
	 * Method to get first sentence from specified string
	 * @param originalString :original value of string
	 * @return String :string value of first sentence
	 */
	String getFirstSentence(String originalString) {
		String sentence = "";
		originalString = substringLinesWithTokenOfEOL(originalString, "  ");
		if (originalString.length() >= 2) {
			for (int i = 0; i < originalString.length(); i++) {
				if (i+2 <= originalString.length()) {
					if (originalString.substring(i,i+1).equals("B")) {
						sentence = originalString.substring(0, i) + "...";
						break;
					}
					if (originalString.substring(i,i+2).equals(". ")) {
						sentence = originalString.substring(0, i+1) + "...";
						break;
					}
					if (originalString.substring(i,i+2).equals("  ")) {
						sentence = originalString.substring(0, i+1) + "...";
						break;
					}
				} else {
					if (originalString.substring(originalString.length()-1,originalString.length()).equals("B")) {
						sentence = originalString.substring(0, originalString.length()-1);
						break;
					} else {
						sentence = originalString;
						break;
					}
				}
			}
		} else {
			sentence = originalString;
		}
		return sentence;
	}
	/**
	 * Method to get String number
	 * @param number :number to be formatted
	 * @return String :formatted value
	 */
	String getFormatted4ByteString(int number) {
		String formattedNumberString = "0000";
		if (0 <= number && number < 10) {formattedNumberString = "000" + number;}
		if (10 <= number && number < 100) {formattedNumberString = "00" + number;}
		if (100 <= number && number < 1000) {formattedNumberString = "0" + number;}
		if (1000 <= number && number < 10000) {formattedNumberString = Integer.toString(number);}
		return formattedNumberString;
	}
	/**
	 * Method to get caret position of text
	 * @param textpane :JTextpane where the caret is placed
	 * @return Point :calculated position of caret
	 */
	Point getCaretPositionInText(JTextPane textpane) {
		int rowNumber = 1;
		Point caretPosition = new Point();
		String text = textpane.getText().substring(0, textpane.getSelectionEnd());
		String lastLine = text;
		//
		for (int i = 0; i < text.length(); i++) {
			if (text.substring(i,i+1).equals("\n")) {
				rowNumber++;
				lastLine = text.substring(i+1, text.length());
			}
		}
		//
		caretPosition.x = rowNumber;
		caretPosition.y = lastLine.getBytes().length;
		//
		return caretPosition;
	}
	/**
	 * Method to get caret position of text
	 * @param textpane :JTextpane where the caret is placed
	 * @param point :caret position
	 * @return int :logical position of caret
	 */
	int getPointPositionInText(JTextPane textpane, Point point) {
		int position = 0;
		int rowNumber = 1;
		String wrkStr1 = "";
		String wrkStr2 = "";
		byte[] byteArray;
		//
		String text = textpane.getText();
		for (int i = 0; i < text.length(); i++) {
			if (point.x == rowNumber) {
				if (point.y == 0) {
					position = i;
				} else {
					for (int j = i; j < text.length(); j++) {
						wrkStr2 = text.substring(j,j+1);
						wrkStr1 = wrkStr1 + wrkStr2;
						byteArray = wrkStr1.getBytes();
						if (byteArray.length >= point.y) {
							position = j + 1;
							break;
						} else {
							if (wrkStr2.equals("\n")) {
								position = j;
								wrkStr1 = "";
								for (int k = byteArray.length; k <= point.y; k++) {
									wrkStr1 = wrkStr1 + " ";
									position++;
								}
								wrkStr1 = wrkStr1 + "\n";
								textpane.select(j, j+1);
								textpane.replaceSelection(wrkStr1);
								break;
							}
						}
					}
				}
				break;
			}
			if (text.substring(i,i+1).equals("\n")) {
				rowNumber++;
			}
		}
		//
		//Process in case that point is on the last line only with "\n"//
		if (!(point.x == 1 && point.y == 0)) {
			if (point.x == rowNumber && position == 0 && text.length() > 0) {
				position = text.length();
			}
		}
		//
		//Process in case additional rows required//
		if (point.x > rowNumber) {
			position = text.length() - 1;
			int additionalLines = point.x - rowNumber;
			for (int i = 0; i < additionalLines; i++) {
				wrkStr1 = "\n";
				position++;
				for (int j = 0; j <= point.y; j++) {
					wrkStr1 = wrkStr1 + " ";
					position++;
				}
				textpane.select(text.length(), text.length() + 1);
				textpane.replaceSelection(wrkStr1);
			}
		}
		//
		return position;
	}
	/**
	 * Drawer of block select rectangle
	 * @param textpane :TextPane component where drawn on
	 */
	void drawRectangleOfBlockSelect(JTextPane textpane) {
		if (inBlockSelectMode && !textpane.getCaret().isSelectionVisible()) {
			textpane.setCaretPosition(textpane.getCaretPosition());
			textpane.getCaret().setSelectionVisible(false);
			blockSelectPointThru = getCaretPositionInText(textpane);
			textpane.paint(savedImage);
			Graphics2D g2 = (Graphics2D)textpane.getGraphics();
			//
			if (blockSelectPointThru.x >= blockSelectPointFrom.x) {
				blockSelectRec.height = blockSelectPointThru.x - blockSelectPointFrom.x + 1;
				blockSelectRec.x = blockSelectPointFrom.x;
				if (blockSelectPointThru.y >= blockSelectPointFrom.y) {
					blockSelectRec.width = blockSelectPointThru.y - blockSelectPointFrom.y;
					blockSelectRec.y = blockSelectPointFrom.y;
				} else {
					blockSelectRec.width = blockSelectPointFrom.y - blockSelectPointThru.y;
					blockSelectRec.y = blockSelectPointThru.y;
				}
			} else {
				blockSelectRec.height = blockSelectPointFrom.x - blockSelectPointThru.x + 1;
				blockSelectRec.x = blockSelectPointThru.x;
				if (blockSelectPointThru.y >= blockSelectPointFrom.y) {
					blockSelectRec.width = blockSelectPointThru.y - blockSelectPointFrom.y;
					blockSelectRec.y = blockSelectPointFrom.y;
				} else {
					blockSelectRec.width = blockSelectPointFrom.y - blockSelectPointThru.y;
					blockSelectRec.y = blockSelectPointThru.y;
				}
			}
			Rectangle visualBlockSelectRec = new Rectangle(0,0,0,0);
			int fontSize = textpane.getFont().getSize();
			if (fontSize == 10) {
				visualBlockSelectRec.height = blockSelectRec.height * 14 - 2;
				visualBlockSelectRec.x = (blockSelectRec.x - 1) * 14 + 2;
				visualBlockSelectRec.width = blockSelectRec.width * 5;
				visualBlockSelectRec.y = blockSelectRec.y * 5 + 2;
			}
			if (fontSize == 12) {
				visualBlockSelectRec.height = blockSelectRec.height * 17 - 2;
				visualBlockSelectRec.x = (blockSelectRec.x - 1) * 17 + 2;
				visualBlockSelectRec.width = blockSelectRec.width * 6;
				visualBlockSelectRec.y = blockSelectRec.y * 6 + 2;
			}
			if (fontSize == 14) {
				visualBlockSelectRec.height = blockSelectRec.height * 20 - 2;
				visualBlockSelectRec.x = (blockSelectRec.x - 1) * 20 + 2;
				visualBlockSelectRec.width = blockSelectRec.width * 7;
				visualBlockSelectRec.y = blockSelectRec.y * 7 + 2;
			}
			g2.setColor(textpane.getSelectionColor());
			g2.drawRect(visualBlockSelectRec.y, visualBlockSelectRec.x, visualBlockSelectRec.width, visualBlockSelectRec.height);
		}
	}
