	/**
	 * Change cursor image according resizing position on IOPanel/IOSpool
	 * @param e :Mouse Event
	 * @param panel :JScrollPane
	 * @return Rectangle :Shape of guide to resize boundary
	 */
	Rectangle setCursorForIOBoundaryResize(MouseEvent e, JScrollPane panel) {
		Rectangle returnRec = null;
		boundaryResizeMode = "";
		//
		Rectangle recBottomLine = new Rectangle(15, 15 + panel.getHeight(), panel.getWidth(), 4);
		Rectangle recRightLine = new Rectangle(15 + panel.getWidth(), 15, 4, panel.getHeight());
		//
		setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
		if (recBottomLine.contains(e.getPoint())) {
			setCursor(new Cursor(Cursor.S_RESIZE_CURSOR));
			boundaryResizeMode = "BOTTOM";
			returnRec = recBottomLine;
		}
		if (recRightLine.contains(e.getPoint())) {
			setCursor(new Cursor(Cursor.E_RESIZE_CURSOR));
			boundaryResizeMode = "RIGHT";
			returnRec = recRightLine;
		}
		//
		return returnRec;
	}
