	/**
	 * Event Handler of mouse-Dragged for jPanelSubjectAreaDataflowEditor2
	 * @param e :Mouse Event
	 */
	void jPanelSubjectAreaDataflowEditor2_mouseDragged(MouseEvent e) {
		if ((e.getModifiers() & InputEvent.BUTTON1_MASK) == InputEvent.BUTTON1_MASK) {
			if (!boundaryResizeMode.equals("")) {
				int posX = dataflowBoundaryEditor.boundaryPosX;
				int posY = dataflowBoundaryEditor.boundaryPosY;
				int width = dataflowBoundaryEditor.boundaryWidth + 1;
				int height = dataflowBoundaryEditor.boundaryHeight + 1;
				//
				Point currentPoint = e.getPoint();
				//
				if (boundaryResizeMode.equals("TOP")) {
					posY = posY - (boundaryResizeStartPoint.y - currentPoint.y);
					height = height + (boundaryResizeStartPoint.y - currentPoint.y);
					if (posY >= 0 && height >= 42) {
						jPanelBoundaryResizeGuide.setLocation(boundaryResizeGuideRec.x,
								boundaryResizeGuideRec.y - (boundaryResizeStartPoint.y - currentPoint.y));
						jPanelBoundaryResizeGuide.repaint();
					}
				}
				if (boundaryResizeMode.equals("BOTTOM")) {
					height = height - (boundaryResizeStartPoint.y - currentPoint.y);
					if (height >= 42) {
						jPanelBoundaryResizeGuide.setLocation(boundaryResizeGuideRec.x,
								boundaryResizeGuideRec.y - (boundaryResizeStartPoint.y - currentPoint.y));
						jPanelBoundaryResizeGuide.repaint();
					}
				}
				if (boundaryResizeMode.equals("LEFT")) {
					posX = posX - (boundaryResizeStartPoint.x - currentPoint.x);
					width = width + (boundaryResizeStartPoint.x - currentPoint.x);
					if (posX >= 0 && width >= 200) {
						jPanelBoundaryResizeGuide.setLocation(boundaryResizeGuideRec.x - (boundaryResizeStartPoint.x - currentPoint.x),
								boundaryResizeGuideRec.y);
						jPanelBoundaryResizeGuide.repaint();
					}
				}
				if (boundaryResizeMode.equals("RIGHT")) {
					width = width - (boundaryResizeStartPoint.x - currentPoint.x);
					if (width >= 200) {
						jPanelBoundaryResizeGuide.setLocation(boundaryResizeGuideRec.x - (boundaryResizeStartPoint.x - currentPoint.x),
								boundaryResizeGuideRec.y);
						jPanelBoundaryResizeGuide.repaint();
					}
				}
				if (boundaryResizeMode.equals("MOVE")) {
					posX = posX - (boundaryResizeStartPoint.x - currentPoint.x);
					posY = posY - (boundaryResizeStartPoint.y - currentPoint.y);
					if (posX > 0 && posY > 0) {
						jPanelBoundaryMoveGuide.setLocation(boundaryResizeGuideRec.x - (boundaryResizeStartPoint.x - currentPoint.x),
								boundaryResizeGuideRec.y - (boundaryResizeStartPoint.y - currentPoint.y));
					}
					if (posX <= 0 && posY > 0) {
						jPanelBoundaryMoveGuide.setLocation(0,
								boundaryResizeGuideRec.y - (boundaryResizeStartPoint.y - currentPoint.y));
					}
					if (posX > 0 && posY <= 0) {
						jPanelBoundaryMoveGuide.setLocation(boundaryResizeGuideRec.x - (boundaryResizeStartPoint.x - currentPoint.x),
								0);
					}
					if (posX <= 0 && posY <= 0) {
						jPanelBoundaryMoveGuide.setLocation(0, 0);
					}
					jPanelBoundaryMoveGuide.repaint();
				}
			}
		}
	}
