	/**
	 * Style controler
	 * @param textpane :JTextPane instance to be processed
	 * @param color :color to be changed to
	 * @param styleType :color to be changed to
	 */
	void changeSelectedStyle(JTextPane textpane, Color color, String styleType) {
		int positionFrom, positionThru;
		StyledDocument sd = textpane.getStyledDocument();
		//
		//Update panel image style//
		if (inBlockSelectMode) {
			Point point = new Point();
			int rowNumber = 1;
			boolean topOfSelection = true;
			//
			String text = textpane.getText();
			for (int i = 0; i < text.length(); i++) {
				if (topOfSelection && rowNumber >= blockSelectRec.x && rowNumber < blockSelectRec.x + blockSelectRec.height) {
					topOfSelection = false;
					point.x = rowNumber;
					point.y = blockSelectRec.y;
					positionFrom = getPointPositionInText(textpane, point);
					point.y = blockSelectRec.y + blockSelectRec.width;
					positionThru = getPointPositionInText(textpane, point);
					Style s1 = sd.addStyle("style1",null);
					if (styleType.equals("BG_COLOR")) {
						StyleConstants.setBackground(s1, color);
					}
					if (styleType.equals("CHAR_COLOR")) {
						StyleConstants.setForeground(s1, color);
					}
					if (styleType.equals("UL")) {
						StyleConstants.setUnderline(s1, true);
					}
					if (styleType.equals("CANCEL_UL")) {
						StyleConstants.setUnderline(s1, false);
					}
					sd.setCharacterAttributes(positionFrom, positionThru - positionFrom, s1, false);
				}
				if (text.substring(i,i+1).equals("\n")) {
					rowNumber++;
					topOfSelection = true;
				}
			}
			//
		} else {
			//
			Style s1 = sd.addStyle("style1",null);
			if (styleType.equals("BG_COLOR")) {
				StyleConstants.setBackground(s1, color);
			}
			if (styleType.equals("CHAR_COLOR")) {
				StyleConstants.setForeground(s1, color);
			}
			if (styleType.equals("UL")) {
				StyleConstants.setUnderline(s1, true);
			}
			if (styleType.equals("CANCEL_UL")) {
				StyleConstants.setUnderline(s1, false);
			}
			positionFrom = textpane.getSelectionStart();
			positionThru = textpane.getSelectionEnd();
			sd.setCharacterAttributes(positionFrom, positionThru - positionFrom, s1, false);
		}
	}
