	/**
	 * Method to copy selected string of textPane
	 * @param textPane :textPane to be processed
	 * @param cutRequired :boolean value to indicate if cut-operation is required
	 */
	void jTextPane_copyAction(JTextPane textPane, boolean cutRequired) {
		if (inBlockSelectMode) {
			Point point = new Point();
			int selectionStartPos = 0;
			int selectionEndPos = 0;
			int rowNumber = 1;
			boolean topOfSelection = true;
			StyledDocument styledDocument = textPane.getStyledDocument();
			//
			styledDocumentCopiedSegmentList.clear();
			styledDocumentCopiedTextList.clear();
			String text = textPane.getText();
			String copiedSegment = "";
			for (int i = 0; i < text.length(); i++) {
				if (topOfSelection && rowNumber >= blockSelectRec.x && rowNumber < blockSelectRec.x + blockSelectRec.height) {
					topOfSelection = false;
					point.x = rowNumber;
					point.y = blockSelectRec.y;
					selectionStartPos = getPointPositionInText(textPane, point);
					point.y = blockSelectRec.y + blockSelectRec.width;
					selectionEndPos = getPointPositionInText(textPane, point);
					copiedSegment = copiedSegment + text.substring(selectionStartPos, selectionEndPos) + "\n";
					//
					styledDocumentCopiedSegmentList.add(new DefaultStyledDocument());
					styledDocumentCopiedTextList.add(text.substring(selectionStartPos, selectionEndPos));
					for (int j = 0; j < styledDocumentCopiedTextList.get(styledDocumentCopiedTextList.size()-1).length(); j++) {
						javax.swing.text.Element textElement = styledDocument.getCharacterElement(j + selectionStartPos);
						try {
							String textElementString = styledDocumentCopiedTextList.get(styledDocumentCopiedTextList.size()-1).substring(j, j+1);
							styledDocumentCopiedSegmentList.get(styledDocumentCopiedTextList.size()-1).insertString(j, textElementString, textElement.getAttributes());
						} catch (BadLocationException ex) {}
					}
				}
				if (text.substring(i,i+1).equals("\n")) {
					rowNumber++;
					topOfSelection = true;
				}
			}
			//
			//Transfer data to the system ClipBoard//
			Clipboard clipboard = getToolkit().getSystemClipboard();
			StringSelection contents = new StringSelection(copiedSegment);
			clipboard.setContents(contents, null);
			//
			if (cutRequired) {
				rowNumber = 1;
				for (int i = 0; i < text.length(); i++) {
					if (topOfSelection && rowNumber >= blockSelectRec.x && rowNumber < blockSelectRec.x + blockSelectRec.height) {
						//
						informationOnThisPageChanged = true;
						topOfSelection = false;
						//
						point.x = rowNumber;
						point.y = blockSelectRec.y;
						selectionStartPos = getPointPositionInText(textPane, point);
						point.y = blockSelectRec.y + blockSelectRec.width;
						selectionEndPos = getPointPositionInText(textPane, point);
						textPane.select(selectionStartPos, selectionEndPos);
						textPane.replaceSelection("");
					}
					if (text.substring(i,i+1).equals("\n")) {
						rowNumber++;
						topOfSelection = true;
					}
				}
			}
			//
		} else {
			//
			if (textPane.getSelectionStart() < textPane.getSelectionEnd()) {
				styledDocumentCopiedSegmentList.clear();
				styledDocumentCopiedSegmentList.add(new DefaultStyledDocument());
				styledDocumentCopiedTextList.clear();
				styledDocumentCopiedTextList.add(textPane.getSelectedText());
				StyledDocument styledDocument = textPane.getStyledDocument();
				for (int i = 0; i < styledDocumentCopiedTextList.get(0).length(); i++) {
					javax.swing.text.Element textElement = styledDocument.getCharacterElement(i + textPane.getSelectionStart());
					try {
						String textElementString = styledDocumentCopiedTextList.get(0).substring(i, i+1);
						styledDocumentCopiedSegmentList.get(0).insertString(i, textElementString, textElement.getAttributes());
					} catch (BadLocationException ex) {}
				}
				//
				if (cutRequired) {
					textPane.replaceSelection("");
					informationOnThisPageChanged = true;
				}
				//
				//Transfer data to the system ClipBoard//
				Clipboard clipboard = getToolkit().getSystemClipboard();
				StringSelection contents = new StringSelection(styledDocumentCopiedTextList.get(0));
				clipboard.setContents(contents, null);
			}
		}
	}
	/**
	 * Method to paste string to textPane
	 * @param textPane :textPane to be processed
	 */
	void jTextPane_pasteAction(JTextPane textPane) {
		Point point = new Point(0,0);
		Point pointWherePasteStart = new Point(0,0);
		int selectionStartPos = 0;
		int selectionEndPos = 0;
		//
		informationOnThisPageChanged = true;
		textPane.requestFocus();
		//
		//Transfer data from the system ClipBoard//
		Clipboard clipboard = getToolkit().getSystemClipboard();
		Transferable contentsReceived = clipboard.getContents(this);
		String clipboardData = "";
		if (contentsReceived != null) {
			if (contentsReceived.isDataFlavorSupported(DataFlavor.stringFlavor)) {
				try {
					clipboardData = (String)contentsReceived.getTransferData(DataFlavor.stringFlavor);
				} catch (Exception ex) {}
			}
		}
		//
		//Remove block select area//
		if (inBlockSelectMode) {
			for (int j = 0; j < blockSelectRec.height; j++) {
				point.x = blockSelectRec.x + j;
				point.y = blockSelectRec.y;
				selectionStartPos = getPointPositionInText(textPane, point);
				point.y = blockSelectRec.y + blockSelectRec.width;
				selectionEndPos = getPointPositionInText(textPane, point);
				textPane.select(selectionStartPos, selectionEndPos);
				textPane.replaceSelection("");
			}
			pointWherePasteStart.x = blockSelectRec.x;
			pointWherePasteStart.y = blockSelectRec.y;
		} else {
			if (textPane.getSelectionStart() < textPane.getSelectionEnd()) {
				textPane.replaceSelection("");
			}
			pointWherePasteStart = getCaretPositionInText(textPane);
		}
		//
		//Paste data to TextPane from ClipBoard or styledDocumentCopiedText//
		String wrkStr = "";
		if (styledDocumentCopiedTextList.size() == 1) {
			wrkStr = styledDocumentCopiedTextList.get(0);
		}
		if (styledDocumentCopiedTextList.size() > 1) {
			for (int k = 0; k < styledDocumentCopiedTextList.size(); k++) {
				wrkStr = wrkStr + styledDocumentCopiedTextList.get(k) + "\n";
			}
		}
		if (clipboardData.equals(wrkStr)) {
			int pastingPos = 0;
			StyledDocument styledDocument = textPane.getStyledDocument();
			for (int j = 0; j < styledDocumentCopiedTextList.size(); j++) {
				point.x = pointWherePasteStart.x + j;
				point.y = pointWherePasteStart.y;
				pastingPos = getPointPositionInText(textPane, point);
				for (int i = 0; i < styledDocumentCopiedTextList.get(j).length(); i++) {
					javax.swing.text.Element textElement = styledDocumentCopiedSegmentList.get(j).getCharacterElement(i);
					try {
						String textElementString = styledDocumentCopiedTextList.get(j).substring(i, i+1);
						styledDocument.insertString(pastingPos, textElementString, textElement.getAttributes());
					} catch (BadLocationException ex) {}
					pastingPos++;
				}
			}
			if (pastingPos > 0) {
				textPane.setCaretPosition(pastingPos);
			}
		} else {
			selectionStartPos = getPointPositionInText(textPane, pointWherePasteStart);
			textPane.select(selectionStartPos, selectionStartPos);
			textPane.replaceSelection(clipboardData);
		}
	}
