	void jTableSystemFunctionTypeList_valueChanged(ListSelectionEvent e) {
		if (tableSystemFunctionTypeListSelectChangeActivated) {
			if (selectedRow_jTableSystemFunctionTypeList != jTableSystemFunctionTypeList.getSelectedRow()) {
				//
				//Update fields of the row previously selected and refresh list//
				if (selectedRow_jTableSystemFunctionTypeList > -1) {
					currentMainTreeNode.updateFieldsForSystem();
				}
				selectedRow_jTableSystemFunctionTypeList = jTableSystemFunctionTypeList.getSelectedRow();
			}
			if (selectedRow_jTableSystemFunctionTypeList >= 0) {
				jTextFieldSystemFunctionTypeSortKey.
				setText((String)tableModelSystemFunctionTypeList.getValueAt(selectedRow_jTableSystemFunctionTypeList,1));
				jTextFieldSystemFunctionTypeSortKey.setBackground(SystemColor.window);
				jTextFieldSystemFunctionTypeSortKey.setEditable(true);
				jTextFieldSystemFunctionTypeName.
				setText((String)tableModelSystemFunctionTypeList.getValueAt(selectedRow_jTableSystemFunctionTypeList,2));
				jTextFieldSystemFunctionTypeName.setBackground(SystemColor.window);
				jTextFieldSystemFunctionTypeName.setEditable(true);
				jTextAreaSystemFunctionTypeDescriptions.
				setText((String)tableModelSystemFunctionTypeList.getValueAt(selectedRow_jTableSystemFunctionTypeList,3));
				jTextAreaSystemFunctionTypeDescriptions.setBackground(SystemColor.window);
				jTextAreaSystemFunctionTypeDescriptions.setEditable(true);
				TableRowNumber tableRowNumber = (TableRowNumber)tableModelSystemFunctionTypeList.
				getValueAt(selectedRow_jTableSystemFunctionTypeList,0);
				//
				//Clear SystemFunctionTypeWhereUsedList//
				if (tableModelSystemFunctionTypeWhereUsedList.getRowCount() > 0) {
					int rowCount = tableModelSystemFunctionTypeWhereUsedList.getRowCount();
					for (int j = 0; j < rowCount; j++) {tableModelSystemFunctionTypeWhereUsedList.removeRow(0);}
				}
				//
				//Add rows to SystemFunctionTypeWhereUsedList//
				XeadTreeNode subsystemNode;
				org.w3c.dom.Element element;
				int m = 0;
				sortableDomElementListModel.removeAllElements();
				NodeList nodeList = domDocument.getElementsByTagName("Function");
				for (int j = 0; j < nodeList.getLength(); j++) {
					element = (org.w3c.dom.Element)nodeList.item(j);
					if (element.getAttribute("FunctionTypeID").equals(tableRowNumber.getElement().getAttribute("ID"))) {
						sortableDomElementListModel.addElement((Object)element);
					}
				}
				sortableDomElementListModel.sortElements();
				for (int j = 0; j < sortableDomElementListModel.getSize(); j++) {
					element = (org.w3c.dom.Element)sortableDomElementListModel.getElementAt(j);
					Object[] Cell = new Object[5];
					m = m + 1;
					Cell[0] =  new TableRowNumber(m, element);
					Cell[1] = element.getAttribute("SortKey");
					Cell[2] = element.getAttribute("Name");
					subsystemNode = getSpecificXeadTreeNode("Subsystem", element.getAttribute("SubsystemID"), null);
					Cell[3] = subsystemNode.getElement().getAttribute("Name");
					Cell[4] = element.getAttribute("Parameters");
					tableModelSystemFunctionTypeWhereUsedList.addRow(Cell);
				}
			}
		}
		if (selectedRow_jTableSystemFunctionTypeList == -1) {
			//
			//Clear and lock edit-panel//
			jTextFieldSystemFunctionTypeName.setText("");
			jTextFieldSystemFunctionTypeName.setBackground(SystemColor.control);
			jTextFieldSystemFunctionTypeName.setEditable(false);
			jTextFieldSystemFunctionTypeSortKey.setText("");
			jTextFieldSystemFunctionTypeSortKey.setBackground(SystemColor.control);
			jTextFieldSystemFunctionTypeSortKey.setEditable(false);
			jTextAreaSystemFunctionTypeDescriptions.setText("");
			jTextAreaSystemFunctionTypeDescriptions.setBackground(SystemColor.control);
			jTextAreaSystemFunctionTypeDescriptions.setEditable(false);
			//
			//Clear WhereUsedList//
			if (tableModelSystemFunctionTypeWhereUsedList.getRowCount() > 0) {
				int rowCount = tableModelSystemFunctionTypeWhereUsedList.getRowCount();
				for (int j = 0; j < rowCount; j++) {tableModelSystemFunctionTypeWhereUsedList.removeRow(0);}
			}
		}
	}
