	/**
	 * Event Handler for jMenuItemComponentToStartSlideShow in case item selected
	 * @param e :Action Event
	 */
	void jMenuItemComponentToStartSlideShow_actionPerformed(ActionEvent e) {
		//
		//Update edited information//
		currentMainTreeNode.updateFieldsForSubjectArea();
		//
		//Setup Slide Show panel//
		//
		//Setup System Boundary//
		jPanelSubjectAreaDataflowSlideShow1.remove(dataflowBoundarySlideShow);
		dataflowBoundarySlideShow = new DataflowBoundary(currentMainTreeNode.getElement());
		jPanelSubjectAreaDataflowSlideShow1.add(dataflowBoundarySlideShow);
		//
		//Clear canvas panel//
		jPanelSubjectAreaDataflowSlideShow2.removeAll();
		//
		//Create ArrayList of DataflowNodes//
		dataflowSlideNumber = 0;
		dataflowSlideTotalNumber = 0;
		DataflowNode dataflowNode = null;
		dataflowNodeSlideShowArray.clear();
		NodeList nodeList = currentMainTreeNode.getElement().getElementsByTagName("DataflowNode");
		for (int i = 0; i < nodeList.getLength(); i++) {
			org.w3c.dom.Element element = (org.w3c.dom.Element)nodeList.item(i);
			dataflowNode = new DataflowNode(element, jPanelSubjectAreaDataflowSlideShow1, jPanelSubjectAreaDataflowSlideShow2, false);
			dataflowNode.setVisible(false);
			jPanelSubjectAreaDataflowSlideShow2.add(dataflowNode, null);
			dataflowNodeSlideShowArray.add(dataflowNode);
			//
			//Set maximum slide number//
			if (dataflowNode.getSlideNumber() > dataflowSlideTotalNumber) {
				dataflowSlideTotalNumber = dataflowNode.getSlideNumber();
			}
		}
		//
		//Set Process number according to slide number//
		int processNumber = 0;
		for (int i = 0; i <= dataflowSlideTotalNumber; i++) {
			for (int j = 0; j < dataflowNodeSlideShowArray.size(); j++) {
				if (dataflowNodeSlideShowArray.get(j).getType().equals("Process")) {
					if (dataflowNodeSlideShowArray.get(j).getSlideNumber() == i) {
						processNumber = processNumber + 1;
						dataflowNodeSlideShowArray.get(j).setNumber(processNumber);
					}
				}
			}
		}
		//
		//Create ArrayList of DataflowLines//
		DataflowLine dataflowLine = null;
		DataflowNode dataflowNode1 = null;
		DataflowNode dataflowNode2 = null;
		dataflowLineSlideShowArray.clear();
		nodeList = currentMainTreeNode.getElement().getElementsByTagName("DataflowLine");
		for (int i = 0; i < nodeList.getLength(); i++) {
			org.w3c.dom.Element element = (org.w3c.dom.Element)nodeList.item(i);
			for (int j = 0; j < dataflowNodeSlideShowArray.size(); j++) {
				if (dataflowNodeSlideShowArray.get(j).getID().equals(element.getAttribute("NodeID1"))) {
					dataflowNode1 = dataflowNodeSlideShowArray.get(j);
				}
				if (dataflowNodeSlideShowArray.get(j).getID().equals(element.getAttribute("NodeID2"))) {
					dataflowNode2 = dataflowNodeSlideShowArray.get(j);
				}
			}
			dataflowLine = new DataflowLine(element, dataflowNode1, dataflowNode2, jPanelSubjectAreaDataflowSlideShow2, false);
			dataflowLine.setVisible(false);
			dataflowLine.addArrows();
			dataflowLineSlideShowArray.add(dataflowLine);
			//
			//Set maximum slide number//
			if (dataflowLine.getSlideNumber() > dataflowSlideTotalNumber) {
				dataflowSlideTotalNumber = dataflowLine.getSlideNumber();
			}
		}
		//
		//Setup Parent panel and dialog panel//
		Dimension dimDialog = jDialogDataflowSlideShow.getSize();
		int posX = 0;
		int posY = 0;
		if (dataflowBoundarySlideShow.boundaryWidth < dimDialog.width) {posX = (dimDialog.width - dataflowBoundarySlideShow.boundaryWidth)/2 - dataflowBoundarySlideShow.boundaryPosX;}
		if (dataflowBoundarySlideShow.boundaryHeight < dimDialog.height) {posY = (dimDialog.height - dataflowBoundarySlideShow.boundaryHeight)/2 - dataflowBoundarySlideShow.boundaryPosY;}
		jPanelSubjectAreaDataflowSlideShow1.setLocation(posX, posY);
		jLabelDataflowSlideShowPageGuide.setText(res.getString("S1846") + dataflowPageTitle + res.getString("S1847") + dataflowSlideNumber + " / " + dataflowSlideTotalNumber + ")");
		jDialogDataflowSlideShow.setVisible(true);
	}
	/**
	 * Event Handler for jMenuItemComponentToAlignTables in case item selected
	 * @param e :Action Event
	 */
	void jMenuItemComponentToAlignTables_actionPerformed(ActionEvent e) {
		int topBoxIndex = 0;
		int intWork;
		float distanceX, distanceY, floatWork;
		float pixelRoundValue = 16; //Note: value of 16 is consequential because of height of jTextAreaShowInstance of DatamodelEntityBox//
		Point point;
		Point topBoxPoint = new Point(0, 800);
		//
		informationOnThisPageChanged = true;
		//
		for (int i = 0; i < datamodelEntityBoxArray.size(); i++) {
			point = datamodelEntityBoxArray.get(i).getBoxPositionPoint();
			if (point.y < topBoxPoint.y) {
				topBoxIndex = i;
				topBoxPoint.x = point.x;
				topBoxPoint.y = point.y;
			}
		}
		//
		for (int i = 0; i < datamodelEntityBoxArray.size(); i++) {
			if (i != topBoxIndex) {
				point = datamodelEntityBoxArray.get(i).getBoxPositionPoint();
				//
				distanceX = point.x - topBoxPoint.x;
				floatWork = distanceX / pixelRoundValue;
				intWork = Math.round(floatWork);
				distanceX = pixelRoundValue * intWork;
				//
				distanceY = point.y - topBoxPoint.y;
				floatWork = distanceY / pixelRoundValue;
				intWork = Math.round(floatWork);
				distanceY = pixelRoundValue * intWork;
				//
				datamodelEntityBoxArray.get(i).changePositionTo((int)(topBoxPoint.x + distanceX), (int)(topBoxPoint.y + distanceY));
			}
		}
		//
		//Repaint image//
		jPanelDatamodel.updateUI();
	}
