	/**
	 * Event Handler for jTableTableFieldList in case mouse released
	 * @param e :Mouse Event
	 */
	void jTableTableFieldList_mouseReleased(MouseEvent e) {
		if ((e.getModifiers() & InputEvent.BUTTON1_MASK) == InputEvent.BUTTON1_MASK) {
			try {
				setCursor(new Cursor(Cursor.WAIT_CURSOR));
				//
				XeadTreeNode tableFieldNode, tableNode;
				TableRowNumber objectNumber, targetNumber, workNumber;
				String workStr = "";
				long workLong = 0;
				//
				workLong = selectedRow_jTableTableFieldList - 1;
				if (selectedRow_jTableTableFieldList != -1 &&
						targetRow_jTableTableFieldList != selectedRow_jTableTableFieldList &&
						targetRow_jTableTableFieldList != workLong) {
					objectNumber = (TableRowNumber)tableModelTableFieldList.getValueAt(selectedRow_jTableTableFieldList, 0);
					//
					targetNumber = (TableRowNumber)tableModelTableFieldList.getValueAt(targetRow_jTableTableFieldList, 0);
					tableNode = (XeadTreeNode)currentMainTreeNode.getParent();
					tableFieldNode = getSpecificXeadTreeNode("TableField", tableNode.getElement().getAttribute("ID"), objectNumber.getElement().getAttribute("ID"));
					//
					//Log TableField node before modified//
					xeadUndoManager.saveNodeBeforeModified(tableFieldNode);
					//
					String sortKeyOfTargetRow = targetNumber.getElement().getAttribute("SortKey");
					//
					if (targetRow_jTableTableFieldList == (tableModelTableFieldList.getRowCount() - 1)) {
						//
						int workInt = Integer.parseInt(sortKeyOfTargetRow) + 10;
						objectNumber.getElement().setAttribute("SortKey", getFormatted4ByteString(workInt));
						//
					} else {
						//
						workNumber = (TableRowNumber)tableModelTableFieldList.getValueAt(targetRow_jTableTableFieldList + 1, 0);
						String sortKeyOfFollowingRow = workNumber.getElement().getAttribute("SortKey");
						int sortKeyBytesOfFollowingRow = sortKeyOfFollowingRow.getBytes().length;
						if (sortKeyBytesOfFollowingRow == 4) {
							sortKeyOfFollowingRow = sortKeyOfFollowingRow + "000000";
						}
						//
						int sortKeyBytesOfTargetRow = sortKeyOfTargetRow.getBytes().length;
						if (sortKeyBytesOfTargetRow == 4) {
							sortKeyOfTargetRow = sortKeyOfTargetRow + "000000";
						}
						//
						workLong = Long.parseLong(sortKeyOfTargetRow) + Long.parseLong(sortKeyOfFollowingRow);
						workLong = workLong / 2;
						if (workLong < 10) {
							workStr = "000000000" + Long.toString(workLong);
						} else {
							if (workLong < 100) {
								workStr = "00000000" + Long.toString(workLong);
							} else {
								if (workLong < 1000) {
									workStr = "0000000" + Long.toString(workLong);
								} else {
									if (workLong < 10000) {
										workStr = "000000" + Long.toString(workLong);
									} else {
										if (workLong < 100000) {
											workStr = "00000" + Long.toString(workLong);
										} else {
											if (workLong < 1000000) {
												workStr = "0000" + Long.toString(workLong);
											} else {
												if (workLong < 10000000) {
													workStr = "000" + Long.toString(workLong);
												} else {
													if (workLong < 100000000) {
														workStr = "00" + Long.toString(workLong);
													} else {
														if (workLong < 1000000000) {
															workStr = "0" + Long.toString(workLong);
														} else {
															if (workLong < 1000000000) {
																workStr = Long.toString(workLong);
															} else {
																workStr = sortKeyOfTargetRow;
															}
														}
													}
												}
											}
										}
									}
								}
							}
						}
						objectNumber.getElement().setAttribute("SortKey", workStr);
					}
					//
					//Log TableField node after modified//
					xeadUndoManager.addLogAfterModified(tableFieldNode);
					//
					//Add item into the array of fields to be renumbered//
					addNodeToTheArrayOfFieldListNodeToBeRenumbered(currentMainTreeNode);
					//
					currentMainTreeNode.activateContentsPane();
				}
				//
			} finally {
				setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
			}
		} else {
			jTableTableFieldList.setRowSelectionInterval(jTableTableFieldList.rowAtPoint(e.getPoint()), jTableTableFieldList.rowAtPoint(e.getPoint()));
			showPopupMenuComponent(e);
		}
	}
