	/**
	 * Method to check if URL is valid or not
	 * @return boolean :whether URL is valid or not
	 */
	boolean urlStringIsValid() {
		boolean reply = false;
		int blaketCheck = 0;
		urlString = "";
		if (lastFocusedTextArea != null) {
			try {
				int caretPosition = lastFocusedTextArea.getCaretPosition();
				int lineNumber = lastFocusedTextArea.getLineOfOffset(caretPosition);
				int startPosOfLine = lastFocusedTextArea.getLineStartOffset(lineNumber);
				int endPosOfLine = lastFocusedTextArea.getLineEndOffset(lineNumber);
				int startPosOfURL = -1;
				int endPosOfURL = -1;
				String endCheck;
				//
				if (startPosOfLine < endPosOfLine) {
					//
					//Calculate startPosOfURL//
					for (int i = caretPosition; i >= startPosOfLine; i--) {
						if (i+5 < endPosOfLine) {
							if (lastFocusedTextArea.getText().substring(i, i+5).equals("http:")
									|| lastFocusedTextArea.getText().substring(i, i+5).equals("file:")) {
								startPosOfURL = i;
								break;
							}
						}
					}
					//
					//Calculate EndPosOfURL//
					for (int i = caretPosition; i <= endPosOfLine; i++) {
						endPosOfURL = i;
						if (i+1 < endPosOfLine) {
							endCheck = lastFocusedTextArea.getText().substring(i, i+1);
							if (endCheck.equals(",") || endCheck.equals(";")) {
								break;
							}
							if (endCheck.equals("(")) {
								blaketCheck++;
							}
							if (endCheck.equals(")")) {
								if (blaketCheck==0) break;
								blaketCheck--;
							}
						}
					}
					//
					if (startPosOfURL > -1 && endPosOfURL > startPosOfURL) {
						urlString = lastFocusedTextArea.getText(startPosOfURL, endPosOfURL - startPosOfURL);
					}
					//
					if (!urlString.equals("")) {
						reply = true;
					}
				}
			}
			catch (BadLocationException ex) {
			}
		}
		return reply;
	}
